import { useRef } from "react";
import { Stack, TextField, Button } from "@mui/material";
import { Link } from "react-router-dom";

const questions = useRef();
const category = useRef();
const difficulty = useRef();
const type = useRef();

const Form = () => {
    return (
        <Stack gap={3} >
            <TextField
                inputRef={questions}
                label="Number of Questions"
                variant="outlined">
                Number of Questions</TextField>
            <TextField 
                inputRef={category}
                label="Category" 
                variant="outlined">
                Category</TextField>
            <TextField 
                inputRef={difficulty}
                label="Difficulty" 
                variant="outlined">
                Difficulty</TextField>
            <TextField 
                inputRef={type}
                label="Type" 
                variant="outlined"
                >Type</TextField>

            <Link to="/game">
            <Button variant="contained" onClick={() => {}}>Start Game</Button>
            </Link>
        </Stack>
    );
};

export default Form;
