import { useContext } from "react";
import { Box, Button, Stack } from "@mui/material";
import { AuthContext } from "../context/Auth";
import Question from "./Question";
import {Link} from "react-router-dom";

const Game = () => {
    const { data } = useContext(AuthContext);
    console.log(data);

let handleData = () => {
    for (let i = 0; i < data.length; i++) {
        console.log(data[i]);
    }
}    


    return (
        <Box>
            <Stack direction={"row"} justifyContent={"right"}>
            <Button sx={{color: "black", background: "yellow", outline: "solid 1px black"}} >Score</Button>
            </Stack>

           <Question />
            <Stack>
                {data.map((questionSet, index) => (
                    <div key={index}>
                        {questionSet.map((question, innerIndex) => (
                            <div key={innerIndex}>
                                <p>Category: {question.category}</p>
                                <p>Question: {question.question}</p>
                               <span>&nbsp;</span>
                            </div>
                        ))}
                    </div>
                ))}
            </Stack>
            <Link to="/game/question">
            <Button on onClick={handleData}>Next Question</Button>
            </Link>
        </Box>
    );
}

export default Game;
