import { useRef, useContext, useState, useEffect } from "react";
import { Stack, TextField, Button, Select, MenuItem, InputLabel, Box, FormControl } from "@mui/material";
import { Link } from "react-router-dom";
import { AuthContext } from "../context/Auth";
import axios from "axios";


const Form = () => {
    const questions = useRef();
    const category = useRef();
    const difficulty = useRef();
    const [value, setValue] = useState('');


    let { startGame } = useContext(AuthContext);

    let hadleData = () => {
        console.log(questions.current.value);
        console.log(category.current.value);
        console.log(difficulty.current.value);


        startGame(questions.current.value, category.current.value, difficulty.current.value);
    }
    const handleChange = (event) => {
        setValue(event.target.value);
      };

    useEffect(() => {
        categories();
    }, []);

    let categories = () => {
        axios.
        get("https://opentdb.com/api_category.php")
        .then((response) => {
            console.log(response.data);
        })
        .catch((error) => {
            console.log(error);
        });
    }

    return (

        <Stack gap={3}>
            <TextField
                inputRef={questions}
                label="Number of Questions"
                variant="outlined">
                Number of Questions</TextField>

            <Box sx={{ minWidth: 120 }} >
                <FormControl fullWidth>
                    <InputLabel id="demo-simple-select-label">Choose Category</InputLabel>
                    <Select
                    labelId="demo-simple-select-label"
                    id="demo-simple-select"
                    value={value}
                    label="Choose Category"
                    onChange={handleChange}
                    inputRef={value}
                   >
                    {categories.map((category) => (
                            <MenuItem key={category.id} value={category.id}>{category.name}</MenuItem>
                        ))}

                    </Select>
                </FormControl>
            </Box>

            <TextField 
                inputRef={difficulty}
                label="Difficulty" 
                variant="outlined">
                Difficulty</TextField>

            <Link to="/game">
            <Button variant="contained" onClick={() => hadleData()}>Start Game</Button>
            </Link>
        </Stack>
    );
};

export default Form;
