                inputRef={category}
                label="Category"
                value={category} 
                variant="outlined">
                <MenuItem value={9}>General Knowledge</MenuItem>
                <MenuItem value={10}>Ten</MenuItem>
                <MenuItem value={20}>Twenty</MenuItem>
                <MenuItem value={30}>Thirty</MenuItem>
            </Select>


            <TextField 
                inputRef={difficulty}
