import { Stack, Typography, Button, TextField, Box } from "@mui/material";
import { createTheme, ThemeProvider } from '@mui/material/styles';

const theme = createTheme({
  components: {
    MuiTextField: {
      styleOverrides: {
        root: {
          '& .MuiOutlinedInput-input': {
            borderWidth: '10rpx', // Adjust this value to make the border thicker
          },
        },
      },
    },
  },
});

const Form = () => {
  return (
    <ThemeProvider theme={theme}>

        <TextField label="Number of Questions" variant="outlined">Number of Questions</TextField>
        <TextField id="outlined-basic" label="Category" variant="outlined">Category</TextField>
        <TextField id="outlined-basic" label="Difficulty" variant="outlined">Difficulty</TextField>
        <TextField id="outlined-basic" label="Type" variant="outlined">Type</TextField>

    </ThemeProvider>
  );
};

export default Form;
