import { useContext, useState } from "react";
import { Box, Button, Stack } from "@mui/material";
import { AuthContext } from "../context/Auth";
import Question from "./Question";
import { Link } from "react-router-dom";

const Game = () => {
    const { data } = useContext(AuthContext);
    const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);

    const handleNextQuestion = () => {
        // Move to the next question index
        setCurrentQuestionIndex(currentQuestionIndex + 1);
    };

    return (
        <Box>
            <Stack direction={"row"} justifyContent={"right"}>
                <Button sx={{ color: "black", background: "yellow", outline: "solid 1px black" }}>Score</Button>
            </Stack>

            {/* Render the current question */}
            <Question question={data[currentQuestionIndex]} />

            <Stack>
                {/* Display current question details */}
                {data[currentQuestionIndex] && (
                    <div>
                        <p>Question: {data[currentQuestionIndex].question}</p>
                        <p>Answer: {data[currentQuestionIndex].correct_answer}</p>
                        <p>Wrong Answers: {data[currentQuestionIndex].incorrect_answers.join(", ")}</p>
                        <span>&nbsp;</span>
                    </div>
                )}
            </Stack>

            {/* Button to navigate to the next question */}
            {currentQuestionIndex < data.length - 1 ? (
                <Button onClick={handleNextQuestion}>Next Question</Button>
            ) : (
                <Link to="/result">
                    <Button>Finish</Button>
                </Link>
            )}
        </Box>
    );
}

export default Game;
