import { Stack, TextField } from "@mui/material";
import { createTheme, ThemeProvider } from '@mui/material/styles';

const theme = createTheme({
  components: {
    MuiOutlinedInput: {
      styleOverrides: {
        root: {
          borderWidth: '5px', // Adjust this value to make the border thicker
        },
      },
    },
  },
});

const Form = () => {
  return (
    <ThemeProvider theme={theme}>
      <Stack gap={3}>
        <TextField label="Number of Questions" variant="outlined" />
        <TextField label="Category" variant="outlined" />
        <TextField label="Difficulty" variant="outlined" />
        <TextField label="Type" variant="outlined" />
      </Stack>
    </ThemeProvider>
  );
};

export default Form;
