import { useRef, useContext, useState, useEffect } from "react";
import { Stack, TextField, Button, Select, MenuItem, InputLabel, Box, FormControl } from "@mui/material";
import { Link } from "react-router-dom";
import { AuthContext } from "../context/Auth";
import axios from "axios";

const Form = () => {
    const questions = useRef();
    const category = useRef();
    const difficulty = useRef();
    const [value, setValue] = useState('');
    const [categories, setCategories] = useState([]);

    let { startGame } = useContext(AuthContext);

    let hadleData = () => {
        startGame(questions.current.value, category.current.value, difficulty.current.value);
    }

    const handleChange = (event) => {
        setValue(event.target.value);
    };

    useEffect(() => {
        fetchCategories();
    }, []);

    let fetchCategories = () => {
        axios.get("https://opentdb.com/api_category.php")
            .then((response) => {
                setCategories(response.data.trivia_categories);
            })
            .catch((error) => {
                console.log(error);
            });
    }

    return (
        <Stack gap={3}>
            <TextField
                inputRef={questions}
                label="Number of Questions"
                variant="outlined">
                Number of Questions
            </TextField>

            <Box sx={{ minWidth: 120 }}>
                <FormControl fullWidth>
                    <InputLabel id="demo-simple-select-label">Choose Category</InputLabel>
                    <Select
                        labelId="demo-simple-select-label"
                        id="demo-simple-select"
                        value={value}
                        label="Choose Category"
                        onChange={handleChange}>
                        {categories.map((category) => (
                            <MenuItem key={category.id} value={category.id} inputRef={category}>{category.name}</MenuItem>
                        ))}
                    </Select>
                </FormControl>
            </Box>

            <Box sx={{ minWidth: 120 }}>
                <FormControl fullWidth>
                    <InputLabel id="demo-simple-select-label">Choose Difficulty</InputLabel>
                    <Select
                        labelId="demo-simple-select-label"
                        id="demo-simple-select"
                        label="Choose Difficulty"
                        inputRef={difficult}>        
                    <MenuItem value="easy">Easy</MenuItem>
                    <MenuItem value="medium">Medium</MenuItem>
                    <MenuItem value="hard">Hard</MenuItem>
                    </Select>
                </FormControl>
            </Box>

            <Link to="/game">
                <Button variant="contained" onClick={hadleData}>Start Game</Button>
            </Link>
        </Stack>
    );
};

export default Form;
