import { useRef, useContext } from "react";
import { Stack, TextField, Button, Select, MenuItem, InputLabel } from "@mui/material";
import { Link } from "react-router-dom";
import { AuthContext } from "../context/Auth";
import axios from "axios";


const Form = () => {
    const questions = useRef();
    const category = useRef();
    const difficulty = useRef();
    const type = useRef();

    let { startGame } = useContext(AuthContext);

    let hadleData = () => {
        console.log(questions.current.value);
        console.log(category.current.value);
        console.log(difficulty.current.value);


        startGame(questions.current.value, category.current.value, difficulty.current.value);
    }

    let categories = () => {
        axios.
        get("https://opentdb.com/api_category.php")
        .then((response) => {
            console.log(response.data);
        })
        .catch((error) => {
            console.log(error);
        });
    }

    return (
        <Stack gap={3} >
            <TextField
                inputRef={questions}
                label="Number of Questions"
                variant="outlined">
                Number of Questions</TextField>
                <InputLabel> Choose Category
                    </InputLabel>
            <Select
                inputRef={category}
                label="Category"
                value={category} 
                variant="outlined">

                   <MenuItem value={10}>Ten</MenuItem>
          <MenuItem value={20}>Twenty</MenuItem>
          <MenuItem value={30}>Thirty</MenuItem></Select>
            <TextField 
                inputRef={difficulty}
                label="Difficulty" 
                variant="outlined">
                Difficulty</TextField>

            <Link to="/game">
            <Button variant="contained" onClick={() => hadleData()}>Start Game</Button>
            </Link>
        </Stack>
    );
};

export default Form;
