import { useContext } from "react";
import { Box, Button } from "@mui/material";
import { AuthContext } from "../context/Auth";


const Game = () => {


    let { data } = useContext(AuthContext);
    console.log(data);
    let newData = data;


    return (
        <Box>
        { newData.map((questionData, index) => (
            <div key={index}>
                <p>Category: {questionData.category}</p>
                <p>Question: {questionData.question}</p>
                <p>Difficulty: {questionData.difficulty}</p>
                <p>Correct Answer: {questionData.correct_answer}</p>

                <p>Type: {questionData.type}</p>
            </div>
        ))}
        <Button>Next Question</Button>
    </Box>
    );
}

export default Game;
