import { AuthContext } from "../context/Auth";
import Question from "./Question";
import {Link} from "react-router-dom";

const Game = () => {
    const { data } = useContext(AuthContext);
    console.log(data);
    const [questions, setQuestions] = useState(0);

let handleQuestion = () => {
    setQuestions(questions + 1);
    console.log(questions);

}    


    return (
        <Box>
            <Stack direction={"row"} justifyContent={"right"}>
            <Button sx={{color: "black", background: "yellow", outline: "solid 1px black"}} >Score</Button>
            </Stack>

           <Question />
            <Stack >
                {data[questions]((data, index) => (
                    <div key={index}>
                        <p>Question: {data.question}</p>
                        <p>Answer: {data.correct_answer}</p>
                        <p>Wrong Answers: {data.incorrect_answers}</p>
                        <span>&nbsp;</span>
                    </div>
                ))}



                {/* {data.map((questionSet, index) => (
                    <div key={index}>
                        {questionSet.map((question, innerIndex) => (
