import { Stack, Typography, Button, TextField, Box } from "@mui/material";
import { createTheme, ThemeProvider } from '@mui/material/styles';

import PlayArrowIcon from '@mui/icons-material/PlayArrow';

const theme = createTheme({
    components: {
      MuiOutlinedInput: {
        styleOverrides: {
          root: {
            '& $notchedOutline': {
              borderColor: 'white', // Adjust this to your desired outline color
            },
            '&:hover $notchedOutline': {
              borderColor: 'white', // Adjust this to your desired hover color
            },
          },
        },
      },
    },
  });

const Form = () => {
    return (
        <Stack gap={3}>
            <ThemeProvider theme={theme}> 
            <TextField id="outlined-basic" label="Number of Questions" variant="outlined">Number of Questions</TextField>
            </ThemeProvider>
            <TextField id="outlined-basic" label="Category" variant="outlined">Category</TextField>
            <TextField id="outlined-basic" label="Difficulty" variant="outlined">Difficulty</TextField>
            <TextField id="outlined-basic" label="Type" variant="outlined">Type</TextField>

        </Stack>
    );
};

export default Form;
