import { Stack, Typography, Button, TextField, Box } from "@mui/material";

import PlayArrowIcon from '@mui/icons-material/PlayArrow';

const theme = createTheme({
    components: {
      MuiOutlinedInput: {
        styleOverrides: {
          root: {
            '& $notchedOutline': {
              borderColor: 'your_desired_color', // Adjust this to your desired outline color
            },
            '&:hover $notchedOutline': {
              borderColor: 'your_hover_color', // Adjust this to your desired hover color
            },
          },
        },
      },
    },
  });

const Form = () => {
    return (
        <Stack gap={3}> 
            <TextField id="outlined-basic" label="Number of Questions" variant="outlined" color="secondary">Number of Questions</TextField>
            <TextField id="outlined-basic" label="Category" variant="outlined">Category</TextField>
            <TextField id="outlined-basic" label="Difficulty" variant="outlined">Difficulty</TextField>
            <TextField id="outlined-basic" label="Type" variant="outlined">Type</TextField>
        </Stack>
    );
};

export default Form;
